import React, { useState, setState } from 'react';
import { FormGroup, Checkbox } from '@material-ui/core';

function CheckboxField(props) {
  const [currentData, setCurrentData] = React.useState([]);
  const [limit, setLimit] = React.useState(2);
  const [checked, setChecked] = React.useState(true);

/*  const selectData = (id, event) => {
      console.log("hey this is checked", event.target.checked);
      console.log("with value", event.target.value);
      let isSelected = event.currentTarget.checked;
      if (isSelected) {
        if (this.state.currentData < this.state.limit) {
          this.setState({ currentData: this.state.currentData+1 });
        }else{
          event.preventDefault()
          event.currentTarget.checked = false;
        }
      } else {
        this.setState({currentData: this.state.currentData - 1});
      };

      setChecked(event.target.checked);
  }*/

  const selectData = React.useCallback(
    (id, event) => {
      console.log("hey this is checked", event.target.checked);
      console.log("with value", event.target.value);
      let isSelected = event.currentTarget.checked;
      if (isSelected) {
        if (currentData < limit) {
          setState({ currentData: currentData+1 });
        }else{
          event.preventDefault()
          event.currentTarget.checked = false;
        }
      } else {
        setState({currentData: currentData - 1});
      };

      setChecked(event.target.checked);
    },
    [], // Tells React to memoize regardless of arguments.
  );

/*  const handleChange = (event) => {
    console.log("hey this is checked", event.target.checked);
    console.log("with value", event.target.value);
    setChecked(event.target.checked);
  };*/
    return (

      <FormGroup style={{flexDirection: 'row'}}>
          <Checkbox
            label='1=(most privileged)'
            iconstyle={{fill: '#000'}}
            value='1'
            //onCheck={this.handleCheck}
            //checked={checked}
            checked={currentData.indexOf()>=0}
            onChange={selectData}
          />
          <Checkbox
            label='2'
            iconstyle={{fill: '#000'}}
            value='2'
            //onCheck={this.handleCheck}
            checked={currentData.indexOf()>=0}
            onChange={selectData}
          />
          <Checkbox
            label='3'
            iconstyle={{fill: '#000'}}
            value='3'
            //onCheck={this.handleCheck}
            checked={currentData.indexOf()>=0}
            onChange={selectData}
          />
          <Checkbox
            label='4'
            iconstyle={{fill: '#000'}}
            value='4'
            //onCheck={this.handleCheck}
            checked={currentData.indexOf()>=0}
            onChange={selectData}
          />
          <Checkbox
            id='5'
            label='5'
            iconstyle={{fill: '#000'}}
            value='5'
            //onCheck={this.handleCheck}
            checked={currentData.indexOf(id)>=0}
            onChange={selectData(id)}
          />
          <Checkbox
            id='6'
            label='6'
            iconstyle={{fill: '#000'}}
            value='6'
            //onCheck={this.handleCheck}
            checked={currentData.indexOf(id)>=0}
            onChange={selectData(id)}
          />
          <Checkbox
            id='7'
            label='7=(Most marginalized)'
            iconstyle={{fill: '#000'}}
            value='7'
            //onCheck={this.handleCheck}
            checked={currentData.indexOf(id)>=0}
            onChange={selectData(id)}
          />
        </FormGroup>
    );
};

export default CheckboxField;
