import React, { useState, useEffect } from 'react';
import { Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import { useDispatch } from 'react-redux';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  withRouter
} from "react-router-dom";

import FormPostContainer from './FormPostContainer';
//import Posts from './components/Posts/Posts';
//import Form from './components/Form/Form';
import { getPosts } from './actions/posts';
import useStyles from './styles';


const Home = () => {
  const [currentId, setCurrentId] = useState(0);
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    dispatch(getPosts());
  }, [currentId, dispatch]);

  return (
    <Router>
      <div>


      <Typography variant="h2" align="center">Social Matrix Project</Typography>
            <Link to="/form">Take the survey here</Link>

        <Switch>
          <Route path="/" component={withRouter(Home)}/>
          <Route path="/form" component={withRouter(FormPostContainer)}/>


        </Switch>
      </div>
    </Router>


  );
};


export default withRouter(Home);
